class Solution {
    fun minSizeSubarray(nums: IntArray, target: Int): Int {
        val prefixSum = LongArray(nums.size + 1)
        for (i in nums.indices) {
            prefixSum[i + 1] = prefixSum[i] + nums[i]
        }
        if (prefixSum.last() == 0L) return -1 // Avoid division by zero

        val totalSum = prefixSum.last()
        var result = Int.MAX_VALUE

        for (i in nums.indices) {
            var left = i + 1
            var right = nums.size
            var possible = false
            while (left <= right) {
                val mid = left + (right - left) / 2
                val sum = prefixSum[mid] - prefixSum[i] + totalSum * ((mid - i) / nums.size)
                if (sum == target.toLong()) {
                    result = minOf(result, mid - i)
                    possible = true
                    break
                } else if (sum < target) {
                    left = mid + 1
                } else {
                    right = mid - 1
                }
            }
            if (!possible && totalSum > 0) {
                val extra = (target - (prefixSum.last() - prefixSum[i])) / totalSum
                val remainder = target - (prefixSum.last() - prefixSum[i]) - totalSum * extra
                if (remainder > 0) {
                    left = 0
                    right = nums.size - 1
                    while (left <= right) {
                        val mid = left + (right - left) / 2
                        val sum = prefixSum[mid + 1]
                        if (sum == remainder) {
                            result = minOf(result, (nums.size - i) + mid + 1 + extra.toInt() * nums.size)
                            break
                        } else if (sum < remainder) {
                            left = mid + 1
                        } else {
                            right = mid - 1
                        }
                    }
                } else if (remainder == 0L) {
                    result = minOf(result, nums.size - i + extra.toInt() * nums.size)
                }
            }
        }
        return if (result == Int.MAX_VALUE) -1 else result
    }
}
