class Solution {
    fun minSizeSubarray(nums: IntArray, target: Int): Int {
        var sum = 0
        var minLen = Int.MAX_VALUE
        var left = 0
        val totalSum = nums.sum()
        if (totalSum == 0) {
            return if (target == 0) 1 else -1
        }
        val cycles = if (totalSum >= target) 2 else (target / totalSum) + 1

        for (i in 0 until nums.size * cycles) {
            sum += nums[i % nums.size]
            while (sum >= target) {
                minLen = minOf(minLen, i - left + 1)
                sum -= nums[left % nums.size]
                left++
            }
        }
        return if (minLen == Int.MAX_VALUE) -1 else minLen
    }
}
