class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $target
     * @return Integer
     */
    function minSizeSubarray($nums, $target) {
        $minLength = PHP_INT_MAX;
        $sum = 0;
        $left = 0;
        $n = count($nums);
        $found = false;

        for ($i = 0; $i < $n; $i++) {
            $sum += $nums[$i];
            while ($sum >= $target) {
                $found = true;
                $minLength = min($minLength, $i - $left + 1);
                $sum -= $nums[$left++];
            }
        }

        // Attempt to find in the circular part
        for ($i = 0; $i < $n - 1; $i++) {
            $sum += $nums[$i % $n];
            while ($sum >= $target) {
                $found = true;
                $minLength = min($minLength, $i - $left + 1 + $n);
                $sum -= $nums[$left++ % $n];
            }
        }

        return $found ? $minLength : -1;
    }
}
