object Solution {
    def minSizeSubarray(nums: Array[Int], target: Int): Int = {
        val n = nums.length
        var sum = 0
        var minLen = Int.MaxValue
        var j = 0

        // Calculate the sum of all elements in nums
        for (i <- nums.indices) sum += nums(i)

        // If the sum is less than target, it's impossible to find such subarray
        if (sum < target) return -1

        sum = 0 // Reset sum to use it for subarray sum calculation

        for (i <- 0 until n * 2) {
            sum += nums(i % n) // Use modulo to simulate infinite array
            while (sum >= target) {
                minLen = math.min(minLen, i - j + 1)
                sum -= nums(j % n)
                j += 1
            }
        }

        if (minLen == Int.MaxValue) -1 else minLen
    }
}
