function minSizeSubarray(nums: number[], target: number): number {
    let totalSum = nums.reduce((a, b) => a + b, 0);
    if (totalSum < target) return -1;

    let ans = Number.MAX_SAFE_INTEGER;
    let prefixSums = new Map();
    prefixSums.set(0, -1);
    let currentSum = 0;

    for (let i = 0; i < nums.length; ++i) {
        currentSum += nums[i];
        if (currentSum >= target) {
            let needed = currentSum - target;
            if (prefixSums.has(needed)) {
                ans = Math.min(ans, i - prefixSums.get(needed));
            }
            if (currentSum === target) ans = Math.min(ans, i + 1);
        }

        // Attempt to build a second round in the prefix sum array to handle circular cases
        for (let j = 0; j < nums.length && currentSum + totalSum - nums[j] >= target; ++j) {
            let newSum = currentSum + totalSum - nums[j];
            let needed = newSum - target;
            if (prefixSums.has(needed)) {
                ans = Math.min(ans, nums.length + i - prefixSums.get(needed) - 1);
            }
            currentSum = newSum;
        }

        prefixSums.set(currentSum, i);
    }

    return ans === Number.MAX_SAFE_INTEGER ? -1 : ans;
}
