public class Solution {
    public int MinimumSum(int[] nums) {
        int n = nums.Length;
        int[] leftMin = new int[n];
        int[] rightMin = new int[n];
        leftMin[0] = int.MaxValue;
        rightMin[n - 1] = int.MaxValue;

        // Fill leftMin
        int min = nums[0];
        for (int i = 1; i < n; i++) {
            leftMin[i] = min;
            if (nums[i] < min) min = nums[i];
        }

        // Fill rightMin
        min = nums[n - 1];
        for (int i = n - 2; i >= 0; i--) {
            rightMin[i] = min;
            if (nums[i] < min) min = nums[i];
        }

        int result = int.MaxValue;
        for (int j = 1; j < n - 1; j++) {
            if (nums[j] > leftMin[j] && nums[j] > rightMin[j]) {
                result = Math.Min(result, leftMin[j] + nums[j] + rightMin[j]);
            }
        }

        return result == int.MaxValue ? -1 : result;
    }
}
