#include <vector>
#include <limits>

class Solution {
public:
    int minimumSum(vector<int>& nums) {
        int n = nums.size();
        int minSum = INT_MAX;
        vector<int> leftMin(n, 0), rightMin(n, 0);

        // Left pass for the minimum value to the left of each element
        int minToLeft = nums[0];
        for (int i = 1; i < n; ++i) {
            leftMin[i] = minToLeft;
            minToLeft = min(minToLeft, nums[i]);
        }

        // Right pass for the minimum value to the right of each element
        int minToRight = nums[n - 1];
        for (int i = n - 2; i >= 0; --i) {
            rightMin[i] = minToRight;
            minToRight = min(minToRight, nums[i]);
        }

        // Find the minimum sum of a mountain triplet
        for (int j = 1; j < n - 1; ++j) {
            if (nums[j] > leftMin[j] && nums[j] > rightMin[j]) {
                int sum = leftMin[j] + nums[j] + rightMin[j];
                minSum = min(minSum, sum);
            }
        }

        return minSum == INT_MAX ? -1 : minSum;
    }
};
