class Solution {
  int minimumSum(List<int> nums) {
    int n = nums.length;
    List<int> leftMin = List.filled(n, 0);
    List<int> rightMin = List.filled(n, 0);
    Set<int> leftSet = {};
    Set<int> rightSet = {};

    leftMin[0] = -1;
    for (int i = 1; i < n; i++) {
      leftMin[i] = (nums[i] > nums[i - 1]) ? i - 1 : leftMin[i - 1];
      if (nums[i] > nums[i - 1]) leftSet.add(i);
    }

    rightMin[n - 1] = -1;
    for (int i = n - 2; i >= 0; i--) {
      rightMin[i] = (nums[i] > nums[i + 1]) ? i + 1 : rightMin[i + 1];
      if (nums[i] > nums[i + 1]) rightSet.add(i);
    }

    int minSum = -1;
    for (int j = 1; j < n - 1; j++) {
      if (leftMin[j] != -1 && rightMin[j] != -1 && leftSet.contains(j) && rightSet.contains(j)) {
        int sum = nums[j] + nums[leftMin[j]] + nums[rightMin[j]];
        if (minSum == -1 || sum < minSum) {
          minSum = sum;
        }
      }
    }
    return minSum;
  }
}
