class Solution {
  int minimumSum(List<int> nums) {
    int n = nums.length;
    List<int>? leftMax = List.filled(n, 0);
    List<int>? rightMax = List.filled(n, 0);
    leftMax[0] = -1;
    rightMax[n - 1] = -1;
    for (int i = 1; i < n; i++) {
      leftMax[i] = nums[i] > nums[i - 1] ? i - 1 : leftMax[i - 1];
    }
    for (int i = n - 2; i >= 0; i--) {
      rightMax[i] = nums[i] > nums[i + 1] ? i + 1 : rightMax[i + 1];
    }

    int minSum = -1;
    for (int j = 1; j < n - 1; j++) {
      if (leftMax[j] != -1 && rightMax[j] != -1 && nums[j] > nums[leftMax[j]] && nums[j] > nums[rightMax[j]]) {
        int sum = nums[j] + nums[leftMax[j]] + nums[rightMax[j]];
        if (minSum == -1 || sum < minSum) {
          minSum = sum;
        }
      }
    }

    return minSum;
  }
}
