func minimumSum(nums []int) int {
    minLeft := make([]int, len(nums))
    minRight := make([]int, len(nums))
    minVal := int(1e9 + 7) // A large value to initialize minimum values
    for i := range nums {
        if i == 0 {
            minLeft[i] = minVal
        } else {
            minLeft[i] = min(minLeft[i-1], nums[i-1])
        }
    }
    for i := len(nums) - 1; i >= 0; i-- {
        if i == len(nums)-1 {
            minRight[i] = minVal
        } else {
            minRight[i] = min(minRight[i+1], nums[i+1])
        }
    }
    minSum := int(1e9 + 7) // A large value to find the minimum sum
    for j := 1; j < len(nums)-1; j++ {
        if nums[j] > nums[j-1] && nums[j] > nums[j+1] {
            sum := nums[j] + min(minLeft[j], minRight[j])
            if sum < minSum {
                minSum = sum
            }
        }
    }
    if minSum == int(1e9+7) {
        return -1
    }
    return minSum
}

func min(a, b int) int {
    if a < b {
        return a
    }
    return b
}
