func minimumSum(nums []int) int {
    n := len(nums)
    leftMin := make([]int, n)
    rightMin := make([]int, n)

    // Initialize leftMin and rightMin with maximum values
    for i := range leftMin {
        leftMin[i] = 1<<31 - 1
    }
    for i := range rightMin {
        rightMin[i] = 1<<31 - 1
    }

    // Fill leftMin with the minimum values to the left of each index
    minVal := nums[0]
    for i := 1; i < n; i++ {
        leftMin[i] = minVal
        if nums[i] < minVal {
            minVal = nums[i]
        }
    }

    // Fill rightMin with the minimum values to the right of each index
    minVal = nums[n-1]
    for i := n - 2; i >= 0; i-- {
        rightMin[i] = minVal
        if nums[i] < minVal {
            minVal = nums[i]
        }
    }

    // Find the minimum sum of a mountain triplet
    minSum := 1<<31 - 1
    for j := 1; j < n-1; j++ {
        if nums[j] > nums[j-1] && nums[j] > nums[j+1] {
            currentSum := nums[j] + leftMin[j] + rightMin[j]
            if leftMin[j] != 1<<31 - 1 && rightMin[j] != 1<<31 - 1 {
                minSum = min(minSum, currentSum)
            }
        }
    }

    if minSum == 1<<31-1 {
        return -1
    }
    return minSum
}

func min(a, b int) int {
    if a < b {
        return a
    }
    return b
}
