class Solution {
    public int minimumSum(int[] nums) {
        int n = nums.length;
        int minSum = Integer.MAX_VALUE;
        int[] leftMax = new int[n];
        int[] rightMax = new int[n];

        leftMax[0] = -1; // Initialize with -1 for no valid left max
        for (int i = 1; i < n; i++) {
            leftMax[i] = -1; // Initialize with -1 for each position
            for (int j = 0; j < i; j++) {
                if (nums[j] < nums[i]) {
                    leftMax[i] = Math.max(leftMax[i], j);
                }
            }
        }

        rightMax[n - 1] = -1; // Initialize with -1 for no valid right max
        for (int i = n - 2; i >= 0; i--) {
            rightMax[i] = -1; // Initialize with -1 for each position
            for (int j = i + 1; j < n; j++) {
                if (nums[j] < nums[i]) {
                    rightMax[i] = Math.max(rightMax[i], j);
                }
            }
        }

        for (int i = 1; i < n - 1; i++) {
            if (leftMax[i] != -1 && rightMax[i] != -1) {
                int sum = nums[leftMax[i]] + nums[i] + nums[rightMax[i]];
                minSum = Math.min(minSum, sum);
            }
        }

        return minSum == Integer.MAX_VALUE ? -1 : minSum;
    }
}
