class Solution {
    public int minimumSum(int[] nums) {
        // Initialize minimum sum as maximum value
        int minSum = Integer.MAX_VALUE;
        // Arrays to store the minimum values on the left and right of each element
        int[] minLeft = new int[nums.length];
        int[] minRight = new int[nums.length];

        // Fill left minimum values with maximum
        for (int i = 0; i < nums.length; i++) {
            minLeft[i] = Integer.MAX_VALUE;
        }
        // Compute minimum values on the left side of each element
        int min = nums[0];
        for (int i = 1; i < nums.length; i++) {
            minLeft[i] = min;
            if (nums[i] < min) {
                min = nums[i];
            }
        }

        // Fill right minimum values with maximum
        for (int i = 0; i < nums.length; i++) {
            minRight[i] = Integer.MAX_VALUE;
        }
        // Compute minimum values on the right side of each element
        min = nums[nums.length - 1];
        for (int i = nums.length - 2; i >= 0; i--) {
            minRight[i] = min;
            if (nums[i] < min) {
                min = nums[i];
            }
        }

        // Find the minimum possible sum of a mountain triplet
        for (int i = 1; i < nums.length - 1; i++) {
            if (minLeft[i] < nums[i] && minRight[i] < nums[i]) { // Check for mountain condition
                int sum = minLeft[i] + nums[i] + minRight[i];
                minSum = Math.min(minSum, sum);
            }
        }

        // If no mountain triplet was found, return -1
        if (minSum == Integer.MAX_VALUE) {
            return -1;
        }
        return minSum;
    }
}
