class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function minimumSum($nums) {
        $n = count($nums);
        $leftMin = array_fill(0, $n, PHP_INT_MAX);
        $rightMin = array_fill(0, $n, PHP_INT_MAX);
        $minSum = PHP_INT_MAX;

        // Calculate minimum value to the left of each element
        $minToLeft = PHP_INT_MAX;
        for ($i = 0; $i < $n; ++$i) {
            $minToLeft = min($minToLeft, $nums[$i]);
            $leftMin[$i] = $minToLeft;
        }

        // Calculate minimum value to the right of each element
        $minToRight = PHP_INT_MAX;
        for ($i = $n - 1; $i >= 0; --$i) {
            $minToRight = min($minToRight, $nums[$i]);
            $rightMin[$i] = $minToRight;
        }

        // Find the minimum sum of mountain triplet
        for ($j = 1; $j < $n - 1; ++$j) {
            if ($nums[$j] > $leftMin[$j - 1] && $nums[$j] > $rightMin[$j + 1]) {
                $minSum = min($minSum, $leftMin[$j - 1] + $nums[$j] + $rightMin[$j + 1]);
            }
        }

        return $minSum == PHP_INT_MAX ? -1 : $minSum;
    }
}
