from typing import List

class Solution:
    def minimumSum(self, nums: List[int]) -> int:
        left_min = [float('inf')] * len(nums)
        right_min = [float('inf')] * len(nums)
        min_sum = float('inf')

        # Update left_min with the minimum value found from the start to the current index
        for i in range(1, len(nums)):
            left_min[i] = min(left_min[i-1], nums[i-1])

        # Update right_min with the minimum value found from the end to the current index
        for i in range(len(nums)-2, -1, -1):
            right_min[i] = min(right_min[i+1], nums[i+1])

        # Find the minimum possible sum of a mountain triplet
        for i in range(1, len(nums)-1):
            if nums[i] > left_min[i] and nums[i] > right_min[i]:
                min_sum = min(min_sum, nums[i] + left_min[i] + right_min[i])

        return min_sum if min_sum != float('inf') else -1
