object Solution {
    def minimumSum(nums: Array[Int]): Int = {
        val n = nums.length
        val leftMin = Array.fill(n)(0)
        val rightMin = Array.fill(n)(0)
        var minSum = Int.MaxValue

        leftMin(0) = -1
        for (i <- 1 until n) {
            leftMin(i) = if (nums(i) > nums(i - 1)) i - 1 else leftMin(i - 1)
        }

        rightMin(n - 1) = -1
        for (i <- n - 2 to 0 by -1) {
            rightMin(i) = if (nums(i) > nums(i + 1)) i + 1 else rightMin(i + 1)
        }

        for (j <- 1 until n - 1) {
            if (leftMin(j) != -1 && rightMin(j) != -1 && nums(j) > nums(leftMin(j)) && nums(j) > nums(rightMin(j))) {
                minSum = Math.min(minSum, nums(j) + nums(leftMin(j)) + nums(rightMin(j)))
            }
        }

        if (minSum == Int.MaxValue) -1 else minSum
    }
}
