class Solution {
    public int sumDistance(int[] nums, String s, int d) {
        final int MOD = 1000000007;
        int n = nums.length;
        long sum = 0;
        long[] left = new long[n];
        long[] right = new long[n];

        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == 'L') {
                left[i] = nums[i] - (long) d;
            } else {
                right[i] = nums[i] + (long) d;
            }
        }

        sort(nums, left, right);

        long totalRight = 0;
        for (int i = 0; i < n; i++) {
            totalRight += right[i];
        }

        long sumLeft = 0, sumRight = totalRight;
        for (int i = 0; i < n; i++) {
            sumRight -= right[i];
            sum = (sum + (right[i] * i - sumLeft) + (sumRight - right[i] * (n - i - 1))) % MOD;
            sumLeft += left[i];
        }

        return (int) (sum % MOD);
    }

    private void sort(int[] nums, long[] left, long[] right) {
        int n = nums.length;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                if (nums[i] > nums[j]) {
                    swap(nums, i, j);
                    swap(left, i, j);
                    swap(right, i, j);
                }
            }
        }
    }

    private void swap(int[] arr, int i, int j) {
        int temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }

    private void swap(long[] arr, int i, int j) {
        long temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }
}
