impl Solution {
    pub fn sum_distance(nums: Vec<i32>, s: String, d: i32) -> i32 {
        let mut lefts = vec![];
        let mut rights = vec![];
        let mod_val = 1_000_000_007;

        for (i, &num) in nums.iter().enumerate() {
            if s.as_bytes()[i] as char == 'L' {
                lefts.push(num);
            } else {
                rights.push(num);
            }
        }

        lefts.sort_unstable();
        rights.sort_unstable();

        let mut total_distance = 0;

        // Calculate distance for left-moving robots
        for &left in &lefts {
            for &right in &rights {
                if right < left {
                    total_distance = (total_distance + left - right - 2 * d) % mod_val;
                } else {
                    break;
                }
            }
        }

        // Calculate distance for right-moving robots
        for &right in &rights {
            for &left in &lefts {
                if right > left {
                    total_distance = (total_distance + right - left) % mod_val;
                } else {
                    total_distance = (total_distance + right - left + 2 * d) % mod_val;
                }
            }
        }

        // Since each pair is counted twice, halve the total distance
        total_distance = if total_distance < 0 { (total_distance + mod_val) % mod_val } else { total_distance };
        ((total_distance as i64 * (nums.len() as i64 - 1) / 2) % mod_val as i64) as i32
    }
}
