#include <stdio.h>
#include <stdlib.h>

const int MOD = 1000000007;

long long modPow(long long base, int exp, int mod) {
    long long result = 1;
    while (exp > 0) {
        if (exp % 2 == 1)
            result = (result * base) % mod;
        base = (base * base) % mod;
        exp /= 2;
    }
    return result;
}

long long factorial(int n, int mod) {
    long long result = 1;
    for (int i = 1; i <= n; i++) {
        result = (result * i) % mod;
    }
    return result;
}

long long nPr(int n, int r, int mod) {
    return factorial(n, mod) * modPow(factorial(n - r, mod), mod - 2, mod) % mod;
}

int stringCount(int n) {
    if (n < 4) return 0;

    // Calculate the total ways to arrange "leet" in a string of length n
    long long waysToArrangeLeet = nPr(n, 4, MOD);

    // Each position not part of "leet" can be any of the 26 letters
    long long remainingPositions = modPow(26, n - 4, MOD);

    // Total good strings is the product of the ways to arrange "leet" and the choices for the remaining positions
    long long totalGoodStrings = (waysToArrangeLeet * remainingPositions) % MOD;

    return (int)totalGoodStrings;
}
