#include <stdbool.h>
#include <string.h>

int removeAlmostEqualCharacters(char* word) {
    int ops = 0;
    for (int i = 1; word[i] != '\0'; ++i) {
        if (word[i] == word[i - 1] || word[i] == word[i - 1] + 1 || word[i] == word[i - 1] - 1) {
            ++ops;
            if (word[i + 1] != '\0') {
                if (word[i + 1] != 'z' && word[i + 1] != 'a') {
                    word[i] = (word[i - 1] != 'a' && word[i + 1] != 'b') ? 'a' : 'z';
                } else {
                    word[i] = (word[i - 1] != 'c' && word[i + 1] != 'c') ? 'c' : 'b';
                }
            } else {
                word[i] = (word[i - 1] != 'a') ? 'a' : 'z';
            }
        }
    }
    return ops;
}
