#include <stdlib.h>

int removeAlmostEqualCharacters(char* word) {
    int count = 0;
    for (int i = 1; word[i] != '\0'; i++) {
        if (word[i] == word[i - 1] || abs(word[i] - word[i - 1]) == 1) {
            count++;
            i++; // Skip next character to avoid double counting
        }
    }
    return count;
}
