impl Solution {
    pub fn remove_almost_equal_characters(word: String) -> i32 {
        let mut ops = 0;
        let bytes = word.as_bytes();
        let mut i = 0;
        while i < bytes.len() {
            let mut j = i;
            while j + 1 < bytes.len() && 
                (bytes[j + 1] == bytes[j] || 
                 (bytes[j + 1] as i8 - bytes[j] as i8).abs() == 1) {
                j += 1;
            }
            let len = j - i + 1;
            if len > 2 {
                ops += (len - 2) / 3 + 1;
            }
            i = j + 1;
        }
        ops
    }
}
