object Solution {
    def removeAlmostEqualCharacters(word: String): Int = {
        var ops = 0
        for(i <- 1 until word.length) {
            if(word(i) == word(i-1) || Math.abs(word(i) - word(i-1)) == 1) {
                ops += 1
                if(i+1 < word.length && (word(i+1) == word(i) || Math.abs(word(i+1) - word(i)) == 1))
                    i += 1
            }
        }
        ops
    }
}
