class Solution {
    fun minimumSteps(s: String): Long {
        var leftBlack = 0
        var rightBlack = s.count { it == '1' }
        var minSteps = Long.MAX_VALUE
        var currentSteps = 0L

        for (i in s.indices) {
            if (s[i] == '1') {
                rightBlack--
            }
            minSteps = minOf(minSteps, currentSteps + rightBlack - leftBlack)
            if (s[i] == '1') {
                leftBlack++
            } else {
                currentSteps += leftBlack
            }
        }
        return if (minSteps == Long.MAX_VALUE) 0L else minSteps
    }
}
