object Solution {
    def minimumSteps(s: String): Long = {
        val n = s.length
        var prefixSum = Array.fill(n+1)(0)
        for (i <- 1 to n) {
            prefixSum(i) = prefixSum(i-1) + (if (s(i-1) == '0') 0 else 1)
        }
        var minSteps = Long.MaxValue
        for (i <- 0 to n) {
            val leftZeros = i - prefixSum(i) // Swaps needed to move zeros to the left
            val rightOnes = prefixSum(n) - prefixSum(i) // Swaps needed to move ones to the right
            minSteps = math.min(minSteps, leftZeros + rightOnes)
        }
        minSteps
    }
}
