public class Solution {
    public string ShortestBeautifulSubstring(string s, int k) {
        int start = 0, onesCount = 0, minLength = int.MaxValue;
        string result = "";

        for (int end = 0; end < s.Length; end++) {
            if (s[end] == '1') {
                onesCount++;
            }

            while (onesCount == k) {
                if (end - start + 1 <= minLength) {
                    string currentSub = s.Substring(start, end - start + 1);
                    if (end - start + 1 < minLength || currentSub.CompareTo(result) < 0) {
                        result = currentSub;
                        minLength = end - start + 1;
                    }
                }

                if (s[start] == '1') {
                    onesCount--;
                }
                start++;
            }
        }

        return result;
    }
}
