#include <string>
#include <vector>

using namespace std;

class Solution {
public:
    string shortestBeautifulSubstring(string s, int k) {
        int n = s.length();
        string result = "";
        int count = 0, left = 0;
        for (int right = 0; right < n; ++right) {
            if (s[right] == '1') ++count;
            if (count > k) {
                while (s[left] != '1') ++left;
                ++left;
                --count;
            }
            if (count == k) {
                string candidate = s.substr(left, right - left + 1);
                if (result.empty() || candidate.length() < result.length() || (candidate.length() == result.length() && candidate < result)) {
                    result = candidate;
                }
                while (left < n && s[left] != '1') ++left;
                ++left;
                --count;
            }
        }
        return result;
    }
};
