#include <string>
#include <vector>
#include <limits>

using namespace std;

class Solution {
public:
    string shortestBeautifulSubstring(string s, int k) {
        int n = s.size();
        int count = 0, start = 0, minLength = INT_MAX;
        string result;

        for (int end = 0; end < n; ++end) {
            if (s[end] == '1') ++count;

            if (count == k) {
                int tempStart = start;
                while (count == k) {
                    if (s[tempStart] == '1') --count;
                    ++tempStart;
                }
                if (end - tempStart + k < minLength) {
                    minLength = end - tempStart + k + 1;
                    result = s.substr(tempStart - 1, minLength);
                }
                start = tempStart;
                count = k - 1;
            }
        }

        return result;
    }
};
