#include <string>
#include <limits>

using namespace std;

class Solution {
public:
    string shortestBeautifulSubstring(string s, int k) {
        int n = s.size();
        int onesCount = 0;
        string result = "";
        int minLength = numeric_limits<int>::max();
        int start = 0, onesFound = 0;

        for (int end = 0; end < n; ++end) {
            onesCount += s[end] == '1';

            while (onesCount == k) {
                if (end - start + 1 <= minLength) {
                    int currentLength = end - start + 1;
                    string currentSub = s.substr(start, currentLength);
                    if (currentLength < minLength || (currentLength == minLength && currentSub < result)) {
                        result = currentSub;
                        minLength = currentLength;
                    }
                }

                onesCount -= s[start] == '1';
                ++start;
            }
        }

        return result;
    }
};
