#include <string.h>
#include <stdlib.h>
#include <limits.h>

char* shortestBeautifulSubstring(char* s, int k) {
    int n = strlen(s);
    int onesCount = 0, minLen = INT_MAX, startIdx = 0;
    char *result = (char*)malloc(sizeof(char));
    result[0] = '\0'; // Initialize result with an empty string

    for (int i = 0, j = 0; i < n; ++i) {
        onesCount += s[i] == '1';
        if (onesCount >= k) {
            while (onesCount > k) {
                onesCount -= s[j++] == '1';
            }
            if (i - j + 1 < minLen) {
                minLen = i - j + 1;
                startIdx = j;
            }
        }
    }

    if (minLen != INT_MAX) {
        free(result); // Free previously allocated memory
        result = (char*)malloc(sizeof(char) * (minLen + 1));
        strncpy(result, s + startIdx, minLen);
        result[minLen] = '\0';
    }

    return result;
}
