class Solution {
    public String shortestBeautifulSubstring(String s, int k) {
        int n = s.length();
        String result = "";
        int[] count = new int[n + 1]; // Prefix sums of 1's
        for (int i = 0; i < n; ++i) {
            count[i + 1] = count[i] + (s.charAt(i) == '1' ? 1 : 0);
        }
        int minLength = Integer.MAX_VALUE;
        for (int i = 0; i <= n; ++i) {
            for (int j = i + 1; j <= n; ++j) {
                if (count[j] - count[i] == k) {
                    int len = j - i;
                    if (len < minLength) {
                        minLength = len;
                        result = s.substring(i, j);
                    } else if (len == minLength) {
                        String current = s.substring(i, j);
                        if (current.compareTo(result) < 0) {
                            result = current;
                        }
                    }
                }
            }
        }
        return result;
    }
}
