var shortestBeautifulSubstring = function(s, k) {
    let result = "", minLen = s.length + 1;
    for (let i = 0; i < s.length; i++) {
        let onesCount = 0;
        for (let j = i; j < s.length; j++) {
            if (s[j] === '1') onesCount++;
            if (onesCount === k) {
                let currentLen = j - i + 1;
                if (currentLen < minLen) {
                    minLen = currentLen;
                    result = s.substring(i, j + 1);
                }
                break; // Found a beautiful substring, move to next starting position
            }
        }
    }
    // Check if a beautiful substring was found, if not return ""
    return minLen <= s.length ? result : "";
};
