var shortestBeautifulSubstring = function(s, k) {
    let minLen = Infinity;
    let result = "";
    for (let i = 0; i <= s.length - k; i++) {
        let ones = 0;
        for (let j = i; j < s.length; j++) {
            if (s[j] === '1') ones++;
            if (ones === k) {
                if (j - i + 1 < minLen) {
                    minLen = j - i + 1;
                    result = s.substring(i, j + 1);
                }
                break; // Found the required ones, no need to go further
            }
        }
    }

    // Filter candidates by length and lexicographically
    let candidates = [];
    for (let i = 0; i <= s.length - minLen; i++) {
        let sub = s.substring(i, i + minLen);
        let onesCount = sub.split('').filter(c => c === '1').length;
        if (onesCount === k) candidates.push(sub);
    }
    candidates.sort(); // Sort to find the lexicographically smallest
    return candidates.length > 0 ? candidates[0] : "";
};
