class Solution {
    fun shortestBeautifulSubstring(s: String, k: Int): String {
        var minLength = Int.MAX_VALUE
        var minSub = ""
        var count = 0
        var left = 0

        for (right in s.indices) {
            if (s[right] == '1') count++
            while (count > k) {
                if (s[left] == '1') count--
                left++
            }
            if (count == k) {
                val len = right - left + 1
                if (len < minLength) {
                    minLength = len
                    minSub = s.substring(left, right + 1)
                } else if (len == minLength) {
                    val currentSub = s.substring(left, right + 1)
                    if (currentSub < minSub) {
                        minSub = currentSub
                    }
                }
            }
        }

        return minSub
    }
}
