class Solution {
    fun shortestBeautifulSubstring(s: String, k: Int): String {
        var result = ""
        val n = s.length
        var onesCount = 0
        var left = 0
        var minLength = Int.MAX_VALUE

        for (right in 0 until n) {
            if (s[right] == '1') onesCount++

            while (onesCount == k) {
                if (right - left + 1 <= minLength) {
                    val candidate = s.substring(left, right + 1)
                    if (minLength > candidate.length || result > candidate) {
                        result = candidate
                        minLength = candidate.length
                    }
                }
                if (s[left] == '1') onesCount--
                left++
            }
        }

        return result
    }
}
