class Solution {

    /**
     * @param String $s
     * @param Integer $k
     * @return String
     */
    function shortestBeautifulSubstring($s, $k) {
        $n = strlen($s);
        $minLen = PHP_INT_MAX;
        $result = "";
        for ($i = 0; $i < $n; $i++) {
            $count = 0;
            for ($j = $i; $j < $n; $j++) {
                if ($s[$j] == '1') $count++;
                if ($count == $k) {
                    $len = $j - $i + 1;
                    if ($len < $minLen) {
                        $minLen = $len;
                        $result = substr($s, $i, $len);
                    } else if ($len == $minLen) {
                        $newSubstr = substr($s, $i, $len);
                        if (strcmp($newSubstr, $result) < 0) {
                            $result = $newSubstr;
                        }
                    }
                    break;
                }
            }
        }
        return $result;
    }
}
