impl Solution {
    pub fn shortest_beautiful_substring(s: String, k: i32) -> String {
        let (mut left, mut count, mut min_len) = (0, 0, usize::MAX);
        let mut result = String::new();
        for right in 0..s.len() {
            if s.as_bytes()[right] == b'1' {
                count += 1;
            }
            while count > k {
                if s.as_bytes()[left] == b'1' {
                    count -= 1;
                }
                left += 1;
            }
            if count == k {
                if right - left + 1 < min_len {
                    min_len = right - left + 1;
                    result = s[left..=right].to_string();
                } else if right - left + 1 == min_len {
                    let current = s[left..=right].to_string();
                    if current < result {
                        result = current;
                    }
                }
            }
        }
        result
    }
}
