impl Solution {
    pub fn shortest_beautiful_substring(s: String, k: i32) -> String {
        let mut result = String::new();
        let mut counts = vec![0; s.len() + 1];
        let mut min_len = usize::MAX;
        let k = k as usize;

        for i in 0..s.len() {
            counts[i + 1] = counts[i] + if s.as_bytes()[i] == b'1' { 1 } else { 0 };
        }

        for i in 0..s.len() {
            if counts[i] + k > counts[s.len()] {
                break;
            }
            let mut low = i;
            let mut high = s.len();
            while low < high {
                let mid = (low + high) / 2;
                if counts[mid + 1] - counts[i] < k {
                    low = mid + 1;
                } else {
                    high = mid;
                }
            }
            let len = high - i + 1;
            if counts[high + 1] - counts[i] == k && len <= min_len {
                let candidate = &s[i..=high];
                if len < min_len || candidate < &result {
                    result = candidate.to_string();
                    min_len = len;
                }
            }
        }

        result
    }
}
