object Solution {
    def shortestBeautifulSubstring(s: String, k: Int): String = {
        val n = s.length
        var minLen = Int.MaxValue
        var result = ""
        var onesCount = 0
        var start = 0
        for (end <- 0 until n) {
            if (s(end) == '1') onesCount += 1
            while (onesCount == k) {
                if (end - start + 1 <= minLen) {
                    val candidate = s.substring(start, end + 1)
                    if (end - start + 1 < minLen || candidate < result) {
                        minLen = end - start + 1
                        result = candidate
                    }
                }
                if (s(start) == '1') onesCount -= 1
                start += 1
            }
        }
        result
    }
}
