class Solution {
    func shortestBeautifulSubstring(_ s: String, _ k: Int) -> String {
        var minLen = Int.max
        var start = 0, end = 0, count = 0
        var answer = ""
        let sArray = Array(s)

        while end < sArray.count {
            if sArray[end] == "1" {
                count += 1
            }

            while count == k {
                if end - start + 1 <= minLen {
                    let candidate = String(sArray[start...end])
                    if end - start + 1 < minLen || candidate < answer {
                        answer = candidate
                        minLen = end - start + 1
                    }
                }

                if sArray[start] == "1" {
                    count -= 1
                }
                start += 1
            }
            end += 1
        }

        return answer
    }
}
