#include <string.h>
#include <stdlib.h>

char * sortVowels(char * s){
    int n = strlen(s);
    char *vowels = (char *)malloc((n + 1) * sizeof(char));
    int vowelCounts[52] = {0}; // 26 lowercase + 26 uppercase

    // Count each vowel.
    for (int i = 0; i < n; i++) {
        char c = s[i];
        if (c >= 'a' && c <= 'z') {
            int index = c - 'a';
            if (c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u') {
                vowelCounts[index]++;
            }
        } else if (c >= 'A' && c <= 'Z') {
            int index = 26 + c - 'A';
            if (c == 'A' || c == 'E' || c == 'I' || c == 'O' || c == 'U') {
                vowelCounts[index]++;
            }
        }
    }

    // Fill the vowels array based on counts.
    int vIndex = 0;
    for (char c = 'A'; c <= 'Z'; c++) {
        int index = 26 + c - 'A';
        while (vowelCounts[index]-- > 0) vowels[vIndex++] = c;
    }
    for (char c = 'a'; c <= 'z'; c++) {
        int index = c - 'a';
        while (vowelCounts[index]-- > 0) vowels[vIndex++] = c;
    }
    vowels[vIndex] = '\0'; // Null-terminate the vowels string.

    // Place sorted vowels back into the original string, keeping consonants in place.
    vIndex = 0;
    for (int i = 0; i < n; i++) {
        if (s[i] == 'a' || s[i] == 'e' || s[i] == 'i' || s[i] == 'o' || s[i] == 'u' ||
            s[i] == 'A' || s[i] == 'E' || s[i] == 'I' || s[i] == 'O' || s[i] == 'U') {
            s[i] = vowels[vIndex++];
        }
    }

    free(vowels);
    return s;
}
