#include <vector>
#include <algorithm>

using namespace std;

class Solution {
    const int MOD = 1e9 + 7;

    int countSpecialPerms(vector<int>& nums, int used, int last, vector<vector<int>>& memo) {
        if (used == (1 << nums.size()) - 1) return 1;
        if (memo[used][last] != -1) return memo[used][last];

        int count = 0;
        for (int i = 0; i < nums.size(); ++i) {
            if (!(used & (1 << i)) && (last == nums.size() || nums[i] % nums[last] == 0 || nums[last] % nums[i] == 0)) {
                count = (count + countSpecialPerms(nums, used | (1 << i), i, memo)) % MOD;
            }
        }
        memo[used][last] = count;
        return count;
    }

public:
    int specialPerm(vector<int>& nums) {
        vector<vector<int>> memo(1 << nums.size(), vector<int>(nums.size() + 1, -1));
        return countSpecialPerms(nums, 0, nums.size(), memo);
    }
};
