/**
 * @param {number[]} nums
 * @return {number}
 */
var specialPerm = function(nums) {
    const mod = 1e9 + 7;
    const n = nums.length;
    const dp = Array.from({ length: 1 << n }, () => 0);
    const fact = [1];
    for (let i = 1; i <= n; i++) fact[i] = (fact[i - 1] * i) % mod;

    for (let i = 0; i < n; i++) dp[1 << i] = 1; // Initialize dp for single elements

    for (let mask = 1; mask < (1 << n); mask++) {
        const bitsSet = countBits(mask);
        if (bitsSet < 2) continue; // Skip masks with less than 2 bits set
        for (let j = 0; j < n; j++) {
            if (!(mask & (1 << j))) continue; // Ensure j is in the current mask
            for (let k = 0; k < n; k++) {
                if (j != k && (mask & (1 << k)) && (nums[j] % nums[k] === 0 || nums[k] % nums[j] === 0)) {
                    dp[mask] += dp[mask ^ (1 << j)];
                    dp[mask] %= mod;
                }
            }
        }
    }

    let result = 0;
    for (let mask = 1; mask < (1 << n); mask++) {
        const bitsSet = countBits(mask);
        if (bitsSet >= 2) {
            result += dp[mask] * fact[bitsSet - 1];
            result %= mod;
        }
    }

    function countBits(x) {
        let bits = 0;
        while (x > 0) {
            bits += x & 1;
            x >>= 1;
        }
        return bits;
    }

    return result;
};
