class Solution {
    fun specialPerm(nums: IntArray): Int {
        val mod = 1000000007
        val n = nums.size
        val dp = Array(1 shl n) { IntArray(n) }

        for (i in 0 until n) dp[1 shl i][i] = 1

        for (mask in 0 until (1 shl n)) {
            for (j in 0 until n) {
                if (mask and (1 shl j) != 0) {
                    for (k in 0 until n) {
                        if (mask and (1 shl k) == 0 && (nums[j] % nums[k] == 0 || nums[k] % nums[j] == 0)) {
                            dp[mask or (1 shl k)][k] = (dp[mask or (1 shl k)][k] + dp[mask][j]) % mod
                        }
                    }
                }
            }
        }

        var ans = 0
        for (i in 0 until n) {
            ans = (ans + dp[(1 shl n) - 1][i]) % mod
        }
        return ans
    }
}
