class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function specialPerm($nums) {
        $mod = 1e9 + 7;
        $n = count($nums);
        $factorial = array_fill(0, $n + 1, 1);
        for ($i = 1; $i <= $n; ++$i) {
            $factorial[$i] = ($factorial[$i - 1] * $i) % $mod;
        }

        $g = array_fill(0, $n, array_fill(0, $n, false));
        for ($i = 0; $i < $n; ++$i) {
            for ($j = 0; $j < $n; ++$j) {
                if ($i != $j && ($nums[$i] % $nums[$j] == 0 || $nums[$j] % $nums[$i] == 0)) {
                    $g[$i][$j] = true;
                }
            }
        }

        $dp = array_fill(0, 1 << $n, 0);
        $dp[0] = 1;
        for ($mask = 1; $mask < (1 << $n); ++$mask) {
            $count = 0;
            for ($i = 0; $i < $n; ++$i) {
                if (($mask & (1 << $i)) > 0) {
                    $count++;
                }
            }
            for ($i = 0; $i < $n; ++$i) {
                if (($mask & (1 << $i)) > 0) {
                    $prevMask = $mask ^ (1 << $i);
                    if ($dp[$prevMask] == 0) continue;
                    $valid = true;
                    for ($j = 0; $j < $n; ++$j) {
                        if ($prevMask & (1 << $j)) {
                            $valid = $valid && $g[$i][$j];
                        }
                    }
                    if ($valid) {
                        $dp[$mask] = ($dp[$mask] + $dp[$prevMask]) % $mod;
                    }
                }
            }
        }

        return $dp[(1 << $n) - 1];
    }
}
