class Solution {
    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function specialPerm($nums) {
        $n = count($nums);
        $mod = 1000000007;

        // Precalculate factorials for later use
        $fact = [1];
        for ($i = 1; $i <= $n; $i++) {
            $fact[$i] = ($fact[$i - 1] * $i) % $mod;
        }

        // Function to count how many numbers a given number is divisible by or divides in the array
        $countDivisible = function($x, $nums) {
            $count = 0;
            foreach ($nums as $num) {
                if ($x % $num == 0 || $num % $x == 0) {
                    $count++;
                }
            }
            return $count;
        };

        // Dynamic programming array
        $dp = array_fill(0, 1 << $n, 0);
        $dp[0] = 1;

        for ($mask = 1; $mask < (1 << $n); $mask++) {
            $pos = bincount($mask);
            for ($i = 0; $i < $n; $i++) {
                if ($mask & (1 << $i)) { // Check if the ith bit is set in the mask
                    $prevMask = $mask ^ (1 << $i);
                    if ($dp[$prevMask] == 0) continue;

                    $divCount = $countDivisible($nums[$i], $nums);
                    if ($divCount >= $pos) {
                        $dp[$mask] = ($dp[$mask] + $dp[$prevMask]) % $mod;
                    }
                }
            }
        }

        return $dp[(1 << $n) - 1];
    }
}

// Helper function to count the number of set bits (1s) in the binary representation of a number
function bincount($n) {
    $count = 0;
    while ($n) {
        $n &= ($n - 1);
        $count++;
    }
    return $count;
}
