from typing import List

class Solution:
    def specialPerm(self, nums: List[int]) -> int:
        n = len(nums)
        mod = 10**9 + 7

        def check(x, y):
            return x % y == 0 or y % x == 0

        # dp[mask][i]: number of special permutations ending with nums[i], represented by mask
        dp = [[0] * n for _ in range(1 << n)]

        for i in range(n):
            dp[1 << i][i] = 1

        for mask in range(1 << n):
            for i in range(n):
                if mask & (1 << i):
                    for j in range(n):
                        if not mask & (1 << j) and check(nums[i], nums[j]):
                            dp[mask | (1 << j)][j] += dp[mask][i]
                            dp[mask | (1 << j)][j] %= mod

        return sum(dp[-1]) % mod
