impl Solution {
    pub fn special_perm(nums: Vec<i32>) -> i32 {
        let modu = 1_000_000_007;
        let n = nums.len();
        let mut dp = vec![vec![0; 1 << n]; n];
        let mut can_follow = vec![vec![false; n]; n];

        for i in 0..n {
            for j in 0..n {
                if nums[i] % nums[j] == 0 || nums[j] % nums[i] == 0 {
                    can_follow[i][j] = true;
                }
            }
        }

        for i in 0..n {
            dp[i][1 << i] = 1;
        }

        for mask in 0..(1 << n) {
            for i in 0..n {
                if mask & (1 << i) != 0 {
                    for j in 0..n {
                        if mask & (1 << j) == 0 && can_follow[i][j] {
                            dp[j][mask | (1 << j)] = (dp[j][mask | (1 << j)] + dp[i][mask]) % modu;
                        }
                    }
                }
            }
        }

        let mut ans = 0;
        for i in 0..n {
            ans = (ans + dp[i][(1 << n) - 1]) % modu;
        }
        ans
    }
}
