object Solution {
    def specialPerm(nums: Array[Int]): Int = {
        val mod = (1e9 + 7).toInt
        val n = nums.length
        val dp = Array.ofDim[Int](1 << n, n)

        for (i <- 0 until n) dp(1 << i)(i) = 1

        for (mask <- 0 until (1 << n); i <- 0 until n if (mask & (1 << i)) > 0) {
            for (j <- 0 until n if (mask & (1 << j)) == 0) {
                if (nums(i) % nums(j) == 0 || nums(j) % nums(i) == 0) {
                    dp(mask | (1 << j))(j) = (dp(mask | (1 << j))(j) + dp(mask)(i)) % mod
                }
            }
        }

        dp((1 << n) - 1).foldLeft(0)((acc, x) => (acc + x) % mod)
    }
}
