class Solution {
    func specialPerm(_ nums: [Int]) -> Int {
        let mod = 1_000_000_007
        let n = nums.count
        var dp = [[Int]](repeating: [Int](repeating: 0, count: 1 << n), count: n)
        var graph = [[Bool]](repeating: [Bool](repeating: false, count: n), count: n)

        for i in 0..<n {
            for j in 0..<n {
                if nums[i] % nums[j] == 0 || nums[j] % nums[i] == 0 {
                    graph[i][j] = true
                }
            }
        }

        for i in 0..<n {
            dp[i][1 << i] = 1
        }

        for mask in 0..<(1 << n) {
            for u in 0..<n where (mask & (1 << u)) != 0 {
                for v in 0..<n where (mask & (1 << v)) == 0 && graph[u][v] {
                    dp[v][mask | (1 << v)] = (dp[v][mask | (1 << v)] + dp[u][mask]) % mod
                }
            }
        }

        var ans = 0
        for i in 0..<n {
            ans = (ans + dp[i][(1 << n) - 1]) % mod
        }

        return ans
    }
}
